use dep::poseidon::poseidon2::Poseidon2;
use crate::constants::{
    MAX_PENDING_ORDER,
    TOTAL_TOKEN,
};

pub struct Order {
    pub price: Field,
    pub quantity: Field,
    pub direction: Field,
    pub token_in: Field,
    pub token_out: Field,
}

pub struct PublicKeyChain {
    pub pk_root: Field,
    pub pk_match: Field,  // Public matching key = Poseidon(sk_match)
    pub nonce: Field,     // Nonce for key rotation (0 for no rotation)
}

pub struct Wallet {
    pub available_balances: [Field; TOTAL_TOKEN],
    pub reserved_balances: [Field; TOTAL_TOKEN],
    pub orders_list: [Field; MAX_PENDING_ORDER],
    pub fees: Field,
    pub keys: PublicKeyChain,  // Public key chain
    pub blinder: Field,        // Randomness for commitment
}

// Shared helper functions
pub fn compute_wallet_commitment(wallet: Wallet) -> Field {
    let available_hash = Poseidon2::hash(wallet.available_balances, TOTAL_TOKEN);
    let reserved_hash = Poseidon2::hash(wallet.reserved_balances, TOTAL_TOKEN);
    let orders_hash = Poseidon2::hash(wallet.orders_list, MAX_PENDING_ORDER);

    // Hash the public key chain (pk_root, pk_match, nonce)
    let keys_hash = Poseidon2::hash(
        [wallet.keys.pk_root, wallet.keys.pk_match, wallet.keys.nonce],
        3
    );

    // Commitment = Hash(balances, orders, keys, fees, blinder)
    Poseidon2::hash(
        [available_hash, reserved_hash, orders_hash, keys_hash, wallet.fees, wallet.blinder],
        6
    )
}

pub fn compute_order_commitment(order: Order) -> Field {
    let order_array = [order.price, order.quantity, order.direction, order.token_in, order.token_out];
    Poseidon2::hash(order_array, 5)
}

