use dep::poseidon::poseidon2::Poseidon2;
use dep::common::constants::{TOTAL_TOKEN, MAX_PENDING_ORDER};
use dep::common::data::{
    Wallet,
    PublicKeyChain,
    compute_wallet_commitment,
};

fn main(
    // Private witness
    pk_root: Field,      // Root public key (Ethereum address or account identifier)
    sk_match: Field,     // Secret matching key (used to derive pk_match)
    initial_blinder: Field,  // Initial random blinder for commitment

    // Public input
    initial_commitment: pub Field
) {
    // Derive pk_match from sk_match
    let pk_match = Poseidon2::hash([sk_match], 1);

    // Initialize empty wallet
    let empty_wallet = Wallet {
        available_balances: [0; TOTAL_TOKEN],
        reserved_balances: [0; TOTAL_TOKEN],
        orders_list: [0; MAX_PENDING_ORDER],
        fees: 0,
        keys: PublicKeyChain {
            pk_root: pk_root,
            pk_match: pk_match,
            nonce: 0,  // Initial nonce is 0
        },
        blinder: initial_blinder,
    };

    // Compute and verify commitment
    let computed_commitment = compute_wallet_commitment(empty_wallet);
    assert(computed_commitment == initial_commitment, "Initial commitment mismatch");
}
